import { useContext } from "react";
import { Store } from "../Store";
import { Helmet } from "react-helmet-async";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import MessageBox from "../components/MessageBox";
import { Link } from "react-router-dom";
import ListGroup from "react-bootstrap/ListGroup";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import axios from "axios";

export default function CartScreen(){
    const {state,dispatch:ctxDispatch} = useContext(Store);
    const {cart : {cartItems},} = state;

    const updateClickHandler = async (item,quantity) =>{
      const data = await axios.get(`/api/products/${item._id}`);
      if(data.countInStock < quantity){
        window.alert("Sorry,Product is out of stock");
        return
      }
      ctxDispatch({
        type:"CART_ADD_ITEM",
        payload:{...item,quantity},
      })
    };

    const deleteCartHandler = async(item) =>{
        ctxDispatch({type:"CART_REMOVE_ITEM",payload:item})
    }

    const checkOutHandler = () =>{
        navigate()
    }

    return(
        <div>
            <Helmet>
                <title>Shopping Cart</title>
            </Helmet>
            <h1>Shopping Cart</h1>
            <Row>
                <Col md={8}>
                    {cartItems.length === 0 ? (
                    <MessageBox>
                       Cart is empty. <Link to="/">Go Shopping</Link>
                    </MessageBox>
                    ):(
                        <ListGroup>
                            {cartItems.map((item) =>(
                                <ListGroup.Item key={item._id}>
                                 <Row className="align-items-center">
                                    <Col md={4}>
                                        <img
                                          src={item.image} alt={item.name} className="img-fluid rounded img-thumbnail">
                                        </img>{' '}
                                        <Link to={`/products/${item.slug}`}>{item.name}</Link>
                                    </Col>
                                    <Col md={3}>
                                        <Button variant="light" disabled={item.quantity === 1} onClick={()=>updateClickHandler(item,item.quantity - 1)}>
                                            <i className="fas fa-minus-circle"></i>
                                        </Button>{' '}
                                        <span>{item.quantity}</span>{' '}
                                        <Button variant="light" disabled={item.quantity === item.countInStock} onClick={() =>updateClickHandler(item,item.quantity+ 1)}>
                                            <i className="fas fa-plus-circle"></i>
                                        </Button>                                    
                                    </Col>
                                    <Col md={3}>${item.price}</Col>
                                    <Col md={2}>
                                        <Button variant="light" onClick={() =>deleteCartHandler(item)}>
                                            <i className="fas fa-trash"></i>
                                        </Button>
                                    </Col>
                                 </Row>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>
                    )}
                </Col>
                <Col md={4}>
                    <Card>
                        <Card.Body>
                            <ListGroup variant="flush">
                                <ListGroup.Item>
                                    <h3>
                                        Subtotal ({cartItems.reduce((a,c) => a + c.quantity,0)}{' '}items) : $ {cartItems.reduce((a,c)=> a + c.price * c.quantity,0)}
                                    </h3>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <div className="d-grid">
                                     <Button type="button" onClick={()=>checkOutHandler()} variant="primary" disabled={cartItems.length === 0}>
                                      Proceed to Checkout
                                     </Button>
                                    </div>
                                </ListGroup.Item>
                            </ListGroup>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </div>
    )
}
